import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import "./App.css";

const socket = io("http://192.168.50.24:3001");

function App() {
    const [ currentImage, setCurrentImage ] = useState("");
    const [ currentAudio, setCurrentAudio ] = useState("");
    const [ playing, setPlaying ] = useState(false);
    const audioRef = useRef(null);

    useEffect(() => {
        socket.on("connect", () => {
            console.log("Connected to server");
        });
        socket.on("connect_error", (error) => {
            console.error("Failed to connect to server", error);
        });
        socket.on("data", (data) => {
            console.log("data:", data);
            if (data === 1) {
                setCurrentImage('/good1.jpg');
                setCurrentAudio('/月光.mp3');
            } else if(data === 0) {
                setCurrentImage('/s0132_11_0.jpg');
                setCurrentAudio('/フルートとハープ.mp3');
            }
        });
        return () => {
            socket.off("connect");
            socket.off("connect_error");
            socket.off("data");
        };
    }, []);

    useEffect(() => {
        if (audioRef.current) {
            audioRef.current.pause();
            audioRef.current.load();
            audioRef.current.play().catch(error => {
                console.error("Failed to play audio:", error);
            });
        }
    }, [currentAudio]);

    const handleAudioEnded = () => {
        setPlaying(false);
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>信号による画像とBGMの切り替え</h1>
                {currentImage && <img src={currentImage} alt="Current" />}
                {currentAudio && (
                    <audio
                        ref={audioRef}
                        src={currentAudio}
                        autoPlay
                        onEnded={handleAudioEnded}
                    >
                        お使いのブラウザはオーディオタグをサポートしていません。
                    </audio>
                )}
            </header>
        </div>
    );
}

export default App;
