import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import "./App.css";

const socket = io("http://192.168.50.24:3001");

const playlog = [];

const MUSIC0 = "/flute_and_harp1.mp3";
const MUSIC1 = "/moonlight1.mp3";
const IMAGE0 = "/images/image1.png";
const IMAGE1 = "/images/image2.png";

function App() {
	const [ currentImage, setCurrentImage ] = useState("");
	const [ currentAudio, setCurrentAudio ] = useState("");
    const [ playing, setPlaying ] = useState(false);
    const audioRef = useRef(null);

    function audioCheck(data, music) {
        if (audioRef.current) {
            audioRef.current.pause();
        }

        if (playlog.length === 1) {
            console.log(`${data}の曲だけ`);
            setCurrentImage(IMAGE0);
            setCurrentAudio(music);
            setPlaying(true);
        } else if (playlog[0] === data && playlog[1] === data) {
            console.log(`${data}の曲を続けて再生`);
            return;
        } else if (playlog[0] !== data && playlog[1] === data) {
            console.log(`${data}の曲を再生`);
            setCurrentImage(IMAGE1);
            setCurrentAudio(music);
            setPlaying(true);
        }

        if (audioRef.current) {
            audioRef.current.load();
            audioRef.current.play().catch(error => {
                console.error("Failed to play audio:", error);
            });
        }
    }

    useEffect(() => {
        socket.on("connect", () => {
            console.log("Connected to server");
        });
        socket.on("connect_error", (error) => {
            console.error("Failed to connect to server", error);
        });
        socket.on("data", (data) => {
            console.log("data:", typeof(data), data);

            if (playlog.length >= 2) {
                playlog.shift();
            }
            if (typeof(data) === "number") {
                playlog.push(data);
            }
            console.log("playlog:", playlog);
            switch (data) {
                case 0:
                    audioCheck(data, MUSIC0);
                    break;
                case 1:
                    audioCheck(data, MUSIC1);
                    break;
                default:
                    console.log(data);
                    break;
            }
        });
        return () => {
            socket.off("connect");
            socket.off("connect_error");
            socket.off("data");
        };
    }, []);

    useEffect(() => {
        if (playing && audioRef.current) {
            audioRef.current.play().catch(error => {
                console.error("Failed to play audio:", error);
            });
        } else if (audioRef.current) {
            audioRef.current.pause();
        }
    }, [playing, currentAudio]);

    const handleAudioEnded = () => {
        setPlaying(false);
    };

	const handlePlayButtonClick = () => {
		if (audioRef.current) {
		  audioRef.current.play().catch(error => {
			console.error("Failed to play audio:", error);
		  });
		}
	};

	return (
		<div className="App">
		  <header className="App-header">
			<h1>信号による画像とBGMの切り替え</h1>
			{currentImage && <img src={currentImage} alt="Current" />}
			{currentAudio && (
			  <audio
				ref={audioRef}
				src={currentAudio}
				autoPlay
				onEnded={handleAudioEnded}
			  >
				お使いのブラウザはオーディオタグをサポートしていません。
			  </audio>
			)}
		  </header>
		</div>
	  );
	}

	export default App;
