import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import "./App.css";

const socket = io("http://192.168.50.24:3001");

function App() {
	const [ currentImage, setCurrentImage ] = useState("");
	const [ currentAudio, setCurrentAudio ] = useState("");
	const [ playing, setPlaying ] = useState(false);
	const audioRef = useRef(null);

	useEffect(() => {
		socket.on("connect", () => {
			console.log("Connected to server");
		});
		socket.on("connect_error", (error) => {
			console.error("Failed to connect to server", error);
		});
		socket.on("data", (data) => {
			console.log("data:", data);
			if (data === 1) {
				console.log("１の曲");
				setCurrentImage('/good1.jpg');
				setCurrentAudio('/月光.mp3');
				setPlaying(true);
			} else if(data === 0) {
				console.log("０の曲");
				setCurrentImage('/s0132_11_0.jpg');
				setCurrentAudio('/フルートとハープ.mp3');
				setPlaying(true);
			}
		});
		return () => {
			socket.off("connect");
			socket.off("connect_error");
			socket.off("data");
		};
	}, []);

	useEffect(() => {
        if (playing && audioRef.current) {
            audioRef.current.play();
        } else if (audioRef.current) {
            audioRef.current.pause();
        }
    }, [playing, currentAudio]);
	return (
		<div className="App">
			<header className="App-header">
			<h1>信号による画像とBGMの切り替え</h1>
			{currentImage && <img src={currentImage} alt="Displayed" className="App-image" />}
			{currentAudio && (
				<audio autoPlay loop>
					<source src={currentAudio} type="audio/mp3" />
					お使いのブラウザはオーディオタグをサポートしていません。
				</audio>
			)}
			</header>
		</div>
	);
}

export default App;
