import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import "./App.css";

const socket = io("http://192.168.10.2:3001");

const playlog = [];
// 各音楽の再生位置を保存
const audioPositions = {
    0: 0,
    1: 0,
    2: 0
};

// 音楽リストの定義
const MUSIC_LISTS = {
    0: [
        { audio: "/くるみ.mp3", image: "/number5.jpg" },
        { audio: "/アリア.mp3", image: "/表示画像6.jpg" },
        { audio: "/gline.mp3", image: "/jupitor.jpg" }
    ],
    1: [
        { audio: "/アリア.mp3", image: "/表示画像6.jpg" },
        { audio: "/gline.mp3", image: "/jupitor.jpg" },
        { audio: "/くるみ.mp3", image: "/number5.jpg" }
    ],
    2: [
        { audio: "/gline.mp3", image: "/jupitor.jpg" },
        { audio: "/くるみ.mp3", image: "/number5.jpg" },
        { audio: "/アリア.mp3", image: "/表示画像6.jpg" }
    ]
};

// 音量の統一設定
const NORMALIZED_VOLUME = 0.5;

function App() {
    const [currentImage, setCurrentImage] = useState("");
    const [currentAudio, setCurrentAudio] = useState("");
    const [playing, setPlaying] = useState(false);
    const [currentMusicId, setCurrentMusicId] = useState(null);
    const [currentPlaylistIndex, setCurrentPlaylistIndex] = useState(0);
    const [currentTime, setCurrentTime] = useState(0);
    const [duration, setDuration] = useState(0);
    const [currentPlaylist, setCurrentPlaylist] = useState(null);
    const audioRef = useRef(null);

    const formatTime = (timeInSeconds) => {
        const minutes = Math.floor(timeInSeconds / 60);
        const seconds = Math.floor(timeInSeconds % 60);
        return `${minutes}:${seconds.toString().padStart(2, '0')}`;
    };

    const handleTimeUpdate = () => {
        if (audioRef.current) {
            setCurrentTime(audioRef.current.currentTime);
        }
    };

    const handleLoadedMetadata = () => {
        if (audioRef.current) {
            setDuration(audioRef.current.duration);
            audioRef.current.currentTime = 0; // 常に最初から再生
            audioRef.current.play().catch(error => {
                console.error("Failed to play audio:", error);
            });
        }
    };

    function startPlaylist(data) {
        const playlist = MUSIC_LISTS[data];
        setCurrentPlaylist(playlist);
        setCurrentPlaylistIndex(0);
        setCurrentMusicId(data);

        // プレイリストの最初の曲を再生
        const firstTrack = playlist[0];
        setCurrentImage(firstTrack.image);
        setCurrentAudio(firstTrack.audio);
        setPlaying(true);
    }

    const playNextInPlaylist = () => {
        if (!currentPlaylist) return;

        const nextIndex = currentPlaylistIndex + 1;
        if (nextIndex < currentPlaylist.length) {
            // まだプレイリストに曲が残っている場合
            setCurrentPlaylistIndex(nextIndex);
            const nextTrack = currentPlaylist[nextIndex];
            setCurrentImage(nextTrack.image);
            setCurrentAudio(nextTrack.audio);
            setPlaying(true);
        } else {
            // プレイリストの最後まで再生完了
            setCurrentPlaylistIndex(0);
            const firstTrack = currentPlaylist[0];
            setCurrentImage(firstTrack.image);
            setCurrentAudio(firstTrack.audio);
            setPlaying(true);
        }
    };

    useEffect(() => {
        socket.on("connect", () => {
            console.log("Connected to server");
        });
        socket.on("connect_error", (error) => {
            console.error("Failed to connect to server", error);
        });
        socket.on("data", (data) => {
            if (playlog.length >= 2) {
                playlog.shift();
            }
            if (typeof(data) === "object" && 'result' in data) {
                playlog.push(data.result);

                // CSVデータを送信（平均値を含む）
                const timestamp = new Date().toISOString();
                const csvData = {
                    timestamp: timestamp,
                    value: data.result,
                    audioPosition: audioPositions[data.result] || 0,
                    meanValue: data.meanValue,
                    currentTrack: currentPlaylistIndex
                };
                socket.emit('saveData', csvData);

                // 新しいプレイリストを開始
                startPlaylist(data.result);
            }
        });
        return () => {
            socket.off("connect");
            socket.off("connect_error");
            socket.off("data");
        };
    }, [currentPlaylistIndex]);

    useEffect(() => {
        if (playing && audioRef.current) {
            audioRef.current.volume = NORMALIZED_VOLUME;
            audioRef.current.play().catch(error => {
                console.error("Failed to play audio:", error);
            });
        } else if (audioRef.current) {
            audioRef.current.pause();
        }
    }, [playing, currentAudio]);

    const handleAudioEnded = () => {
        // 曲が終わったら次の曲を再生
        playNextInPlaylist();
    };

    const handlePlayButtonClick = () => {
        if (audioRef.current) {
            audioRef.current.volume = NORMALIZED_VOLUME;
            audioRef.current.play().catch(error => {
                console.error("Failed to play audio:", error);
            });
        }
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>信号による画像とBGMの切り替え</h1>
                {currentImage && <img src={currentImage} alt="Current" />}
                {currentAudio && (
                    <>
                        <audio
                            ref={audioRef}
                            src={currentAudio}
                            onEnded={handleAudioEnded}
                            onTimeUpdate={handleTimeUpdate}
                            onLoadedMetadata={handleLoadedMetadata}
                        >
                            お使いのブラウザはオーディオタグをサポートしていません。
                        </audio>
                        <div className="audio-controls">
                            <button onClick={handlePlayButtonClick}>再生</button>
                            <div className="time-display">
                                <span>{formatTime(currentTime)}</span>
                                <span> / </span>
                                <span>{formatTime(duration)}</span>
                            </div>
                            <div className="playlist-info">
                                再生中: {currentPlaylistIndex + 1} / {currentPlaylist?.length || 0}
                            </div>
                        </div>
                    </>
                )}
            </header>
        </div>
    );
}

export default App;
