import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import "./App.css";

const socket = io("http://192.168.50.24:3001");

let playlog = [];

function App() {
	const [ currentImage, setCurrentImage ] = useState("");
	const [ currentAudio, setCurrentAudio ] = useState("");
	const [ playing, setPlaying ] = useState(false);

	function audioCheck(data) {
		if (playlog.length === 1) {
			console.log("playlog[0]:データ0だけ");
			//setCurrentAudio("");
		} else if (playlog[1] === data) {
			console.log(`playlog[1]:${data}の曲を続けて再生`);
			return;
		} else if (playlog[1] !== data) {
			console.log(`${data}の曲を再生`);
			//setCurrentAudio("");
		}
	}

	useEffect(() => {
		socket.on("connect", () => {
			console.log("Connected to server");
		});
		socket.on("connect_error", (error) => {
			console.error("Failed to connect to server", error);
		});
		socket.on("data", (data) => {
			console.log("data:", typeof(data), data);

			if (playlog.length >= 2) {
				playlog.delete();
			}
			console.log("playlog:", playlog);
			playlog.push(data);
			switch (data) {
				case 0:
					audioCheck(data);
					break;
				case 1:
					audioCheck(data);
					break;
				default:
					break;
			}
		});
		return () => {
			socket.off("connect");
			socket.off("connect_error");
			socket.off("data");
		};
	}, []);
	return (
		<div className="App">
			<header className="App-header">
			<h1>信号による画像とBGMの切り替え</h1>
			{currentImage && <img src={currentImage} alt="Displayed" className="App-image" />}
			{currentAudio && (
				<audio autoPlay loop>
					<source src={currentAudio} type="audio/mp3" />
					お使いのブラウザはオーディオタグをサポートしていません。
				</audio>
			)}
			</header>
		</div>
	);
}

export default App;
